import React, { useState } from 'react';
import Layout from '../components/Layout';
import './Profile.css';

const Profile = () => {
  const [activeTab, setActiveTab] = useState('account');
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    firstName: 'Jessica',
    lastName: '',
    email: 'Jessica@gmail.com',
    phone: '+923326753554'
  });
  const [passwordData, setPasswordData] = useState({
    currentPassword: '',
    newPassword: '',
    confirmPassword: ''
  });

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handlePasswordChange = (e) => {
    setPasswordData({
      ...passwordData,
      [e.target.name]: e.target.value
    });
  };

  const handleSaveAccount = () => {
    // Save account information
    alert('Account information saved successfully!');
    setIsEditing(false);
  };

  const handleSavePassword = () => {
    if (!passwordData.currentPassword || !passwordData.newPassword || !passwordData.confirmPassword) {
      alert('Please fill in all fields');
      return;
    }

    if (passwordData.newPassword !== passwordData.confirmPassword) {
      alert('New passwords do not match');
      return;
    }

    if (passwordData.newPassword.length < 6) {
      alert('New password must be at least 6 characters long');
      return;
    }

    alert('Password changed successfully!');
    setPasswordData({
      currentPassword: '',
      newPassword: '',
      confirmPassword: ''
    });
  };

  const handleCancel = () => {
    setIsEditing(false);
    // Reset form data if needed
  };

  return (
    <Layout title="Profile">
      <div className="p-4">
        {/* Profile Header Card */}
        <div className="profile-header">
          <div className="d-flex justify-content-between align-items-center">
            <div className="d-flex align-items-center">
              <div className="profile-avatar me-4">
                <i className="fas fa-user fa-3x text-white"></i>
              </div>
              <div>
                <h3 className="text-white fw-bold mb-2">{formData.firstName}</h3>
                <p className="text-gray mb-0">{formData.email}</p>
              </div>
            </div>
            <div className="rank-badge">
              <i className="fas fa-shield-alt text-white"></i>
            </div>
          </div>
        </div>

        {/* Profile Form Card */}
        <div className="dashboard-card">
          {/* Tabs */}
          <div className="profile-tabs">
            <button 
              className={`profile-tab ${activeTab === 'account' ? 'active' : ''}`}
              onClick={() => setActiveTab('account')}
            >
              Account
            </button>
            <button 
              className={`profile-tab ${activeTab === 'password' ? 'active' : ''}`}
              onClick={() => setActiveTab('password')}
            >
              Change Password
            </button>
          </div>

          {/* Account Section */}
          {activeTab === 'account' && (
            <div id="account-tab" className="tab-content active">
              <div className="d-flex justify-content-between align-items-center mb-4">
                <h5 className="text-white fw-bold mb-0">Account</h5>
                {!isEditing && (
                  <button className="btn-edit" onClick={() => setIsEditing(true)}>
                    Edit
                  </button>
                )}
              </div>

              {/* Form */}
              <form id="account-form">
                <div className="row">
                  {/* First Name */}
                  <div className="col-md-6">
                    <div className="form-group">
                      <label className="form-label">First Name</label>
                      <div className="position-relative">
                        <i className="far fa-user input-icon"></i>
                        <input 
                          type="text" 
                          className="form-input form-input-with-icon" 
                          id="first-name" 
                          name="firstName"
                          value={formData.firstName}
                          onChange={handleInputChange}
                          readOnly={!isEditing}
                        />
                      </div>
                    </div>
                  </div>
                  {/* Last Name */}
                  <div className="col-md-6">
                    <div className="form-group">
                      <label className="form-label">Last Name</label>
                      <div className="position-relative">
                        <i className="far fa-user input-icon"></i>
                        <input 
                          type="text" 
                          className="form-input form-input-with-icon" 
                          id="last-name" 
                          name="lastName"
                          placeholder="Enter your Last Name"
                          value={formData.lastName}
                          onChange={handleInputChange}
                          readOnly={!isEditing}
                        />
                      </div>
                    </div>
                  </div>
                  {/* Email */}
                  <div className="col-md-6">
                    <div className="form-group">
                      <label className="form-label">Email</label>
                      <div className="position-relative">
                        <i className="far fa-envelope input-icon"></i>
                        <input 
                          type="email" 
                          className="form-input form-input-with-icon" 
                          id="email" 
                          name="email"
                          value={formData.email}
                          onChange={handleInputChange}
                          readOnly={!isEditing}
                        />
                      </div>
                    </div>
                  </div>
                  {/* Phone */}
                  <div className="col-md-6">
                    <div className="form-group">
                      <label className="form-label">Phone</label>
                      <div className="position-relative">
                        <i className="fas fa-phone input-icon"></i>
                        <input 
                          type="tel" 
                          className="form-input form-input-with-icon" 
                          id="phone" 
                          name="phone"
                          value={formData.phone}
                          onChange={handleInputChange}
                          readOnly={!isEditing}
                        />
                      </div>
                    </div>
                  </div>
                </div>

                {/* Save/Cancel Buttons */}
                {isEditing && (
                  <div className="mt-4" id="account-form-actions">
                    <button type="button" className="btn-save" onClick={handleSaveAccount}>
                      Save Changes
                    </button>
                    <button type="button" className="btn-cancel" onClick={handleCancel}>
                      Cancel
                    </button>
                  </div>
                )}
              </form>
            </div>
          )}

          {/* Change Password Section */}
          {activeTab === 'password' && (
            <div id="password-tab" className="tab-content active">
              <div className="d-flex justify-content-between align-items-center mb-4">
                <h5 className="text-white fw-bold mb-0">Change Password</h5>
              </div>

              {/* Password Form */}
              <form id="password-form">
                <div className="row">
                  {/* Current Password */}
                  <div className="col-md-6">
                    <div className="form-group">
                      <label className="form-label">Current Password</label>
                      <div className="position-relative">
                        <i className="fas fa-lock input-icon"></i>
                        <input 
                          type="password" 
                          className="form-input form-input-with-icon" 
                          id="current-password" 
                          name="currentPassword"
                          placeholder="Enter current password"
                          value={passwordData.currentPassword}
                          onChange={handlePasswordChange}
                        />
                      </div>
                    </div>
                  </div>
                  {/* New Password */}
                  <div className="col-md-6">
                    <div className="form-group">
                      <label className="form-label">New Password</label>
                      <div className="position-relative">
                        <i className="fas fa-lock input-icon"></i>
                        <input 
                          type="password" 
                          className="form-input form-input-with-icon" 
                          id="new-password" 
                          name="newPassword"
                          placeholder="Enter new password"
                          value={passwordData.newPassword}
                          onChange={handlePasswordChange}
                        />
                      </div>
                    </div>
                  </div>
                  {/* Confirm Password */}
                  <div className="col-md-6">
                    <div className="form-group">
                      <label className="form-label">Confirm Password</label>
                      <div className="position-relative">
                        <i className="fas fa-lock input-icon"></i>
                        <input 
                          type="password" 
                          className="form-input form-input-with-icon" 
                          id="confirm-password" 
                          name="confirmPassword"
                          placeholder="Confirm new password"
                          value={passwordData.confirmPassword}
                          onChange={handlePasswordChange}
                        />
                      </div>
                    </div>
                  </div>
                </div>

                {/* Save/Cancel Buttons */}
                <div className="mt-4">
                  <button type="button" className="btn-save" onClick={handleSavePassword}>
                    Save Changes
                  </button>
                  <button 
                    type="button" 
                    className="btn-cancel" 
                    onClick={() => setPasswordData({currentPassword: '', newPassword: '', confirmPassword: ''})}
                  >
                    Cancel
                  </button>
                </div>
              </form>
            </div>
          )}
        </div>
      </div>
    </Layout>
  );
};

export default Profile;
