import React from 'react';
import { useNavigate } from 'react-router-dom';
import Layout from '../components/Layout';
import './Profile.css';

const Profile = () => {
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem('isAuthenticated');
    window.location.href = '/';
  };

  const menuItems = [
    {
      icon: 'fas fa-user',
      label: 'Profile',
      path: '/profile'
    },
    {
      icon: 'fas fa-chart-line',
      label: 'Investment Plans',
      path: '/investment-plans'
    },
    {
      icon: 'fas fa-wallet',
      label: 'Wallet',
      path: '/wallet'
    },
    {
      icon: 'fas fa-mobile-alt',
      label: 'Deposit',
      path: '/add-funds'
    },
    {
      icon: 'fas fa-money-bill-wave',
      label: 'Withdraw',
      path: '/withdraw-funds'
    },
    {
      icon: 'fas fa-medal',
      label: 'Ranks',
      path: '/goals'
    },
    {
      icon: 'fas fa-users',
      label: 'Referral',
      path: '/referrals'
    },
    {
      icon: 'fas fa-file-alt',
      label: 'Transaction History',
      path: '/transactions'
    }
  ];

  return (
    <Layout title="Profile">
      <div className="profile-page-container">
        {/* Profile Header Card */}
        <div className="profile-header-card">
          <div className="profile-header-content">
            <div className="profile-avatar-wrapper">
              <img 
                src="/img/customer-img-1.jpg" 
                alt="Profile" 
                className="profile-avatar-img"
                onError={(e) => {
                  e.target.style.display = 'none';
                  e.target.nextSibling.style.display = 'flex';
                }}
              />
              <div className="profile-avatar-placeholder" style={{ display: 'none' }}>
                <i className="fas fa-user"></i>
              </div>
            </div>
            <div className="profile-info">
              <h3 className="profile-name">Jessica</h3>
              <p className="profile-email">Jessica@gmail.com</p>
              <p className="profile-uid">UID: JESSICA1234</p>
            </div>
          </div>
        </div>

        {/* Menu Items List */}
        <div className="profile-menu-list">
          {menuItems.map((item, index) => (
            <div
              key={index}
              className="profile-menu-item"
              onClick={() => navigate(item.path)}
            >
              <div className="menu-item-left">
                <i className={`${item.icon} menu-item-icon`}></i>
                <span className="menu-item-label">{item.label}</span>
              </div>
              <i className="fas fa-chevron-right menu-item-arrow"></i>
            </div>
          ))}
        </div>

        {/* Logout Button */}
        <div className="profile-logout-wrapper">
          <button className="profile-logout-btn" onClick={handleLogout}>
            Logout
          </button>
        </div>
      </div>
    </Layout>
  );
};

export default Profile;
